import { ActivatedRoute, Router } from '@angular/router';
import { MovieService } from './../movie.service';
import { Component } from '@angular/core';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent {
  movie = {
    title: "",
    gender: 0,
    duration: 0
  }
  constructor(
    private service: MovieService,
    private router: Router,
    private route: ActivatedRoute
    ){}
  ngOnInit(): void{
    const id = this.route.snapshot.paramMap.get('id');
    this.service.getMovieById(parseInt(id!)).subscribe((movie) => {
      this.movie = movie;
    });
  }
  OnSaveMovie(): void{
    this.service.putMovie(this.movie).subscribe((result) => {
      alert("Movie edited")
      this.router.navigate([`/movies/${result.id}`]);
    });
  }
  OnCancel(): void {
    this.router.navigate(['/movies'])
  }
}
<div class="container">
  <div class="row">
    <div class="col-6 border border-light rounded p-5 shadow">
      <form class="form">
        <div class="form-group">
          <label for="movieTitle">Movie Title</label>
          <input type="text"
                id="movieTitle"
                name="movieTitle"
                placeholder="Type the movie title"
                class="form-control"
                [value]="movie.title" />
        </div>
        <div class="form-group">
          <label for="movieGender">Movie Gender</label>
          <input type="text"
                id="movieGender"
                name="movieGender"
                placeholder="Type the movie gender"
                class="form-control"
                [value]="movie.gender" />
        </div>
        <div class="form-group">
          <label for="movieDuration">Movie Duration</label>
          <input type="text"
                id="movieDuration"
                name="movieDuration"
                placeholder="Type the movie duration"
                class="form-control"
                [value]="movie.duration" />
        </div>
      </form>
    </div>
  </div>
</div>
<button (click)="OnSaveMovie()" class="btn btn-success">Save</button>
<button (click)="OnCancel()" class="btn btn-danger">Cancel</button>
