import { ActivatedRoute, Router } from '@angular/router';
import { MovieService } from './../movie.service';
import { Component, Input } from '@angular/core';
import { IMovie } from '../movie';
import { FormGroup, FormControl, NgControlStatus } from '@angular/forms';

@Component({
  selector: 'app-movie',
  templateUrl: './movie.component.html',
  styleUrls: ['./movie.component.css']
})
export class MovieComponent {
  @Input()
  pageName: string = "";

  movieForm: any;
  isDisabled: boolean = false;
  constructor(
    private service: MovieService,
    private route: ActivatedRoute,
    private router: Router
  ){}
  ngOnInit(){
    const id = this.route.snapshot.paramMap.get("id");
    this.service.getMovieById(parseInt(id!)).subscribe((result) => {
      this.FillMovieForm(result);
    })
    this.isDisabled = this.pageName;
  }
  OnEditMovie(id: number){
    this.router.navigate([`/movies/edit/${id}`], { relativeTo: this.route })
  }
  FillMovieForm(result: IMovie){
    this.movieForm = new FormGroup({
      title: new FormControl({value: result.title, disabled: this.isDisabled}),
      gender: new FormControl({value: result.gender, disabled: this.isDisabled}),
      duration: new FormControl({value: result.duration, disabled: this.isDisabled})
    });
  }
  OnSaveMovie(): void{
    this.service.putMovie(this.movieForm).subscribe((result) => {
      alert("Movie edited")
      this.router.navigate([`/movies/${result.id}`]);
    });
  }
  OnCancel(): void {
    this.router.navigate(['/movies'])
  }
}
<div class="container">
  <div class="row">
    <div class="col-6 border border-light rounded p-5 shadow">
      <form class="form" (submit)="OnSaveMovie()">
        <div class="form-group">
          <label for="movieTitle">Movie Title</label>
          <input type="text"
                id="movieTitle"
                name="movieTitle"
                placeholder="Type the movie title"
                class="form-control"
                [value]="movieForm.title" />
        </div>
        <div class="form-group">
          <label for="movieGender">Movie Gender</label>
          <input type="text"
                id="movieGender"
                name="movieGender"
                placeholder="Type the movie gender"
                class="form-control"
                [value]="movieForm.gender" />
        </div>
        <div class="form-group">
          <label for="movieDuration">Movie Duration</label>
          <input type="text"
                id="movieDuration"
                name="movieDuration"
                placeholder="Type the movie duration"
                class="form-control"
                [value]="movieForm.duration" />
        </div>
        <div class="form-group mt-2">
        <button type="submit" class="btn btn-success">Save</button>
        <button (click)="OnCancel()" class="btn btn-danger">Cancel</button>
      </div>
      </form>
    </div>
  </div>
</div>
