import { Component, Input } from '@angular/core';
import { Movie } from '../movie';
import { MovieService } from '../movie.service';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent {
  @Input() movie: Movie = {
    title: '',
    gender: 0,
    duration: 0
  };

  constructor(private service: MovieService){}
  NgOnCreateMovie(){
    var teste = this.service.postMovies(this.movie);
    if(teste){
      alert("Movie created" + teste)
    }
  }
}
<form class="form">
  <div class="form-group">
    <label for="movieTitle"></label>
    <input id="movieTitle" name="movieTitle"/>
  </div>
  <div class="form-group">
    <label for="movieGender"></label>
    <input id="movieGender" name="movieGender"/>
  </div>
  <div class="form-group">
    <label for="movieDuration"></label>
    <input id="movieDuration" name="movieDuration"/>
  </div>
</form>
<button routerLink="/details" (click)="NgOnCreateMovie()">Salvar</button>
