import { Component } from '@angular/core';
import { Movie } from '../movie';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent {

  movie = {
    title: "Teste Title",
    gender: 1,
    duration: 100
  }

  constructor(){}

  ngOnInit() : void{
    this.movie
  }
}
<form>
  <label for="movieTitle">Movie Title</label>
  <input type="text"
         class="input"
         id="movieTitle"
         name="movieTitle"
         placeholder="Type the movie title"
         class="form-group"
         [(ngModel)]="movie.title" />
  <p>{{ movie.title }}</p>
</form>
<button routerLink="/edit">Edit Movie</button>
<app-delete />
