import { MovieComponent } from './../movie.component';
import { ActivatedRoute, Router } from '@angular/router';
import { Component } from '@angular/core';
import { MovieService } from 'src/app/services/movie.service';
import { IMovie } from 'src/app/models/Movie';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  movieList: IMovie[] = [];
  displayedColumns: string[] = ['title', 'gender', 'duration', 'actions'];

  constructor(
    private service: MovieService,
    private router: Router,
    private route: ActivatedRoute,
    public dialogRef: MatDialogRef<ListComponent>,
    @Inject(MAT_DIALOG_DATA) public data: IMovie
    ){}

  ngOnInit() : void{
    this.service.getMovies().subscribe((result) => {
      this.movieList = result;
    });
  }
  openDialog() {
    const dialogRef = this.dialog.open(MovieComponent);

    dialogRef.afterClosed().subscribe(result => {
      console.log(`Dialog result: ${result}`);
    });
  }
  OnEditMovie(id?: number): void{
    const editUrl = '/movies/edit/' + id!;
    this.router.navigate([editUrl], { relativeTo: this.route});
  }
  OnDeleteMovie(id?: number){
    if(confirm("Do you really wanna delete this movie?")){
      this.service.deleteMovies(id!).subscribe((result) => {
        window.location.reload();
      });
    }
  }
}
