import { MovieService } from 'src/app/services/movie.service';
import { HttpClient } from '@angular/common/http';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { Component, Inject, OnInit } from '@angular/core';
import IMovie from 'src/app/models/Movie';

@Component({
  selector: 'app-movie-dialog',
  templateUrl: './movie-dialog.component.html',
  styleUrls: ['./movie-dialog.component.css']
})
export class MovieDialogComponent implements OnInit {
  isChange!: boolean;
  constructor(
    private service: MovieService,
    public MatDialogRef: MatDialogRef<IMovie>,
    @Inject(MAT_DIALOG_DATA) public data: IMovie,
  ) { }

  ngOnInit(): void{
    console.log(this.data.id)
    this.isChange = this.data.id != 0;
  }
  onCreate(){
    this.service.postMovie(this.data);
  }
  onSave(){
    this.service.putMovie(this.data);
  }
  onCancel(){
    this.MatDialogRef.close();
  }
}
<h1 *ngIf="!isChange" mat-dialog-title>Create Product</h1>
<h1 *ngIf="isChange" mat-dialog-title>Update Product</h1>
<div mat-dialog-content>
  <mat-form-field>
    <mat-label for="movieTitle">Movie Title</mat-label>
      <input matInput [(ngModel)]="data.title"/>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Movie Gend</mat-label>
    <input matInput type="number" [(ngModel)]="data.gender"/>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Movie Duration</mat-label>
    <input matInput type="number" [(ngModel)]="data.duration"/>
  </mat-form-field>
</div>
<div mat-dialog-actions>
    <button *ngIf="!isChange" mat-button (click)="onCreate()" mat-button-color="primary" [mat-dialog-close]="data" cdkFocusInitial>Create</button>
    <button *ngIf="isChange" mat-button (click)="onSave()" mat-button-color="primary" [mat-dialog-close]="data" cdkFocusInitial>Save</button>
  <button mat-button (click)="onCancel()">Cancel</button>
</div>
