import { Component, Input, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { Movie } from '../movie';
import { MovieService } from '../movie.service';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent implements OnInit{

  form: any;

  constructor(
    private service: MovieService,
    private router: Router
  ){}

  ngOnInit(): void{
    this.form = new FormGroup({
      title: new FormControl(null),
      gender: new FormControl(null),
      duration: new FormControl(null)
    });
  }

  NgOnCreateMovie(){
    this.service.postMovies(this.movie).subscribe(() => {
      this.router.navigate(['/movies/details']);
    });
  }
}
<form class="form">
  <div class="form-group">
    <label for="movieTitle"></label>
    <input id="movieTitle" name="movieTitle" [value]="movie.title"/>
  </div>
  <div class="form-group">
    <label for="movieGender"></label>
    <input id="movieGender" name="movieGender" [value]="movie.gender"/>
  </div>
  <div class="form-group">
    <label for="movieDuration"></label>
    <input id="movieDuration" name="movieDuration" [value]="movie.duration"/>
  </div>
</form>
<button routerLink="/details" (click)="NgOnCreateMovie()">Salvar</button>
