import { ActivatedRoute, Router } from '@angular/router';
import { MovieService } from './../movie.service';
import { Component, Input } from '@angular/core';
import { IMovie } from '../movie';
import { FormGroup, FormControl } from '@angular/forms';

@Component({
  selector: 'app-movie',
  templateUrl: './movie.component.html',
  styleUrls: ['./movie.component.css']
})
export class MovieComponent {
  movieForm = any;
  constructor(
    private service: MovieService,
    private route: ActivatedRoute,
    private router: Router
  ){}
  ngOnInit(){
    const id = this.route.snapshot.paramMap.get("id");
    this.service.getMovieById(parseInt(id!)).subscribe((result) => {
      this.FillMovieForm(result);
    })
  }
  OnEditMovie(id: number){
    this.router.navigate([`/movies/edit/${id}`], { relativeTo: this.route})
  }
  FillMovieForm(result: IMovie){
    this.movieForm = new FormGroup({
      title: new FormControl(result.title),
      gender: new FormControl(result.gender),
      duration: new FormControl(result.duration)
    });
  }
}
<div class="container p-5">
  <div class="row">
    <div class="col-6 border border-light rounded p-5 shadow" *ngIf="movieForm">
      <h6>{{movieForm.title}}</h6>
      <hr/>
      <form [formGroup]="movieForm">
        <div class="form-group">
          <label>Title</label>
          <input enabled="false" class="form-control form-control-sm" formControlName="title"/>
        </div>
        <div class="form-group">
          <label>Gender</label>
          <input enabled="false" class="form-control form-control-sm" formControlName="gender"/>
        </div>
        <div class="form-group">
          <label>Duration</label>
          <input enabled="false" class="form-control form-control-sm" formControlName="duration"/>
        </div>
        <div class="container">
          <div class="row mt-2 justify-content-around">
            <div class="col-6">
              <button type="submit" class="btn btn-sm btn-outline-secondary" (click)="OnEditM
              (movie.id)">Edit Movie</button>
            </div>
            <div class="col-6 pl-1">
              <button type="button" class="btn btn-sm btn-light" (click)="OnCancelCreating()">Cancel</button>
            </div>
          </div>
        </div>
      </form>
    </div>
  </div>
