import { Movie } from './movie';
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http'
import { Observable, Observer } from 'rxjs';

const httpOptions = {
  headers: new HttpHeaders({
    'Content-Type': 'application/json'
  })
}

@Injectable({
  providedIn: 'root'
})
export class MovieService {
  private readonly apiSource = 'https://localhost:7016/api/v1/Movies';

  constructor(private http: HttpClient) {
  }
  getMovies(): Observable<Movie[]>{
    return this.http.get<Movie[]>(this.apiSource, httpOptions);
  }
  getMovieById(id: number): Observable<Movie>{
    const url = `${this.apiSource}/${id}`
    return this.http.get<Movie>(url, httpOptions);
  }
  postMovies(movie: Movie): Observable<any>{
    return this.http.post<Movie>(this.apiSource, movie, httpOptions);
  }
  putMovie(movie: Movie): Observable<any>{
    const url = `${this.apiSource}/${movie.id}`
    return this.http.put<Movie>(url, movie, httpOptions);
  }
  deleteMovies(id: number): Observable<any>{
    const url = `${this.apiSource}/${id}`
    return this.http.delete<Movie>(url, httpOptions);
  }
}
