import { Movie } from './movie';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http'
import { Observable, Observer } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class MovieService {
  private readonly apiSource = 'http://localhost:5021/v1/movies';

  constructor(private http: HttpClient) {
  }
  getMovies(): Observable<Movie[]>{
    return this.http.get<Movie[]>(this.apiSource);
  }
  getMovieById(id: number): Observable<Movie>{
    const url = `${this.apiSource}/${id}`
    return this.http.get<Movie>(url);
  }
  postMovies(movie: Movie): Observable<Movie>{
    return this.http.post<Movie>(this.apiSource, movie);
  }
  putMovie(movie: Movie): Observable<Movie>{
    const url = `${this.apiSource}/${id}`
    return this.http.put<Movie>(url, movie);
  }
  deleteMovies(id: number): Observable<Movie>{
    const url = `${this.apiSource}/${id}`
    return this.http.delete<Movie>(url);
  }
}
