import { ActivatedRoute, Router, Routes } from '@angular/router';
import { MovieService } from './../movie.service';
import { Component } from '@angular/core';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent {
  movie = {
    title: "",
    gender: 0,
    duration: 0
  }
  constructor(
    private service: MovieService,
    private router: Router,
    private route: ActivatedRoute
    ){}
  NgOnInit(): void{
    const id = this.route.snapshot.paramMap.get('id');
    this.service.getMovieById(parseInt(id!)).subscribe((movie) => {
      this.movie = movie;
    });
  }
  NgOnSaveMovie(): void{
    this.service.putMovie(this.movie).subscribe(() => {
      this.router.navigate(['/details']);
    });
  }
  OnCancel() {
    this.router.navigate(['/listarPensamento'])
}
}
<div class="container">
  <form class="form">
    <div class="form-group">
      <label for="movieTitle">Movie Title</label>
      <input type="text"
            id="movieTitle"
            name="movieTitle"
            placeholder="Type the movie title"
            class="form-control"
            [(ngModel)]="movie.title" />
    </div>
    <div class="form-group">
      <label for="movieGender">Movie Gender</label>
      <input type="text"
            id="movieGender"
            name="movieGender"
            placeholder="Type the movie gender"
            class="form-control"
            [(ngModel)]="movie.gender" />
    </div>
    <div class="form-group">
      <label for="movieDuration">Movie Duration</label>
      <input type="text"
            id="movieDuration"
            name="movieDuration"
            placeholder="Type the movie duration"
            class="form-control"
            [(ngModel)]="movie.duration" />
    </div>
  </form>
</div>
<button (click)="NgOnSaveMovie()"></button>
<app-delete />
