import { ActivatedRoute, Router } from '@angular/router';
import { MovieService } from './../movie.service';
import { Component } from '@angular/core';
import { IMovie } from '../movie';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent {

  movieList: IMovie[] = [];

  constructor(
    private service: MovieService,
    private router: Router,
    private route: ActivatedRoute
    ){}

  ngOnInit() : void{
    this.service.getMovies().subscribe((result) => {
      console.log(result);
      console.log(result[1]);
      this.movieList = result;
    });
  }
  OnEditMovie(id: number): void{
    const editUrl = '/movies/edit/' + id;
    this.router.navigate([editUrl], { relativeTo: this.route});
  }
  OnDeleteMovie(id: number){
    if(confirm("Do you really wanna delete this movie?")){
      this.service.deleteMovies(id).subscribe((result) => {
        window.location.reload();
      });
    }
  }
}
