import { Component, Input, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { Movie } from '../movie';
import { MovieService } from '../movie.service';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent implements OnInit{

  movieForm: any;

  constructor(
    private service: MovieService,
    private router: Router
  ){}

  ngOnInit(): void{
    this.movieForm = new FormGroup({
      title: new FormControl(null),
      gender: new FormControl(null),
      duration: new FormControl(null)
    });
  }

  NgOnCreateMovie(){
    this.service.postMovies(this.movieForm).subscribe(() => {
      this.router.navigate(['/movies/details']);
    });
  }
}
<div class="container p-5">
  <div class="row">
    <div class="col-6 border border-light rounded p-5 shadow" *ngIf="movieForm">
      <h6>{{movieForm.title}}</h6>
      <hr/>
      <form [formGroup]="movieForm">
        <div class="form-group">
          <label>Title</label>
          <input class="form-control form-control-sm" formControlName="title"/>
        </div>
        <div class="form-group">
          <label>Gender</label>
          <input class="form-control form-control-sm" formControlName="gender"/>
        </div>
        <div class="form-group">
          <label>Duration</label>
          <input class="form-control form-control-sm" formControlName="duration"/>
        </div>
        <div class="container">
          <div class="row">
            <div>
              <button type="submit" class="btn btn-sm btn-outline-secondary">Save</button>
            </div>
            <div class="pl-1">
              <button type="button" class="btn btn-sm btn-light">Cancel</button>
            </div>
          </div>
        </div>
      </form>
    </div>
  </div>
<button routerLink="/details" (click)="NgOnCreateMovie()">Salvar</button>
