import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { Component, Inject, OnInit } from '@angular/core';
import IMovie from 'src/app/models/Movie';

@Component({
  selector: 'app-movie-dialog',
  templateUrl: './movie-dialog.component.html',
  styleUrls: ['./movie-dialog.component.css']
})
export class MovieDialogComponent implements OnInit {

  constructor(
    public MatDialogRef: MatDialogRef<IMovie>,
    @Inject(MAT_DIALOG_DATA) public data: IMovie,
  ) { }

  ngOnInit() {
  }
  onCancel(){
  }
}
<h1 mat-dialog-title>Create Product</h1>
<mat-form-field>
  <mat-label for="movieTitle">Movie Title</mat-label>
    <input matInput [(ngModel)]="data.title"/>
</mat-form-field>
<mat-form-field>
  <mat-label>Movie Gender</mat-label>
  <input matInput [(ngModel)]="data.gender"/>
</mat-form-field>
<mat-form-field>
  <mat-label>Movie Duration</mat-label>
  <input matInput [(ngModel)]="data.duration"/>
</mat-form-field>
<mat-form-field>
    <ng-container *ngIf="pageName=='Create' else DetailsPage">
      <div class="form-group mt-2">
        <button type="submit" class="btn btn-success">Create</button>
        <button (click)="onCancel()" class="btn btn-danger">Cancel</button>
      </div>
    </ng-container>
    <ng-template #EditPage>
      <div class="form-group mt-2">
        <button type="submit" class="btn btn-success">Save</button>
        <button (click)="onCancel()" class="btn btn-danger">Cancel</button>
      </div>
    </ng-template>
</mat-form-field>
